import React from 'react';
import ReactDOM from 'react-dom';
import { Button, Input, AutoComplete } from 'choerodon-ui';

const Option = AutoComplete.Option;
const OptGroup = AutoComplete.OptGroup;

const dataSource = [{
  title: '话题',
