import React from 'react';
import ReactDOM from 'react-dom';
import { Transfer } from 'choerodon-ui';

const mockData = [];
for (let i = 0; i < 20; i += 1) {
  mockData.push({
    key: i.toString(),
    title: `content${i + 1}`,
    description: `description of content${i + 1}`,
    disabled: i % 3 < 1,
  });
}

