examples.source:
import React from 'react';
import ReactDOM from 'react-dom';
import { DataSet, TreeSelect, Row, Col, Button } from 'choerodon-ui/pro';

function handleDataSetChange({ record, name, value, oldValue }) {
  console.log(
    '[dataset newValue]',
    value,
    '[oldValue]',
    oldValue,
    `[record.get('${name}')]`,
    record.get(name),
  );
}

class App extends React.Component {
  optionDs = new DataSet({
    selection: 'single',
    queryUrl: '/tree.mock',
    autoQuery: true,
    idField: 'id',
    parentField: 'parentId',
  });

  ds = new DataSet({
    fields: [
      {
        name: 'user',
        type: 'number',
        multiple: true,
        textField: 'text',
        valueField: 'id',
        label: '用户',
        options: this.optionDs,
      },
    ],
    events: {
      update: handleDataSetChange,
    },
  });

  changeOptions = () => {
    this.ds.addField('account', {
      name: 'account',
      type: 'number',
      multiple: true,
      textField: 'text',
      valueField: 'id',
      label: '账户',
      options: this.optionDs,
    });
  };

  render() {
    return (
      <Row gutter={10}>
        <Col span={8}>
          <TreeSelect dataSet={this.ds} name="user" />
        </Col>
        <Col span={8}>
          <TreeSelect dataSet={this.ds} name="account" />
        </Col>
        <Col span={8}>
          <Button onClick={this.changeOptions}>切换选项</Button>
        </Col>
      </Row>
    );
  }
}

ReactDOM.render(<App />, document.getElementById('container'));

