examples.source:
import React from 'react';
import ReactDOM from 'react-dom';
import { useModal, Button, ModalProvider } from 'choerodon-ui/pro';

const maskStyle = {
  backgroundColor: 'rgb(0, 193, 255,.3)',
};

const App = () => {
  const Modal = useModal();

  const openModal = React.useCallback(
    (mask = true) => {
      Modal.open({
        title: 'Customized mask',
        children: (
          <div>
            <p>Some contents...</p>
            <p>Some contents...</p>
            <p>Some contents...</p>
          </div>
        ),
        okText: '确定',
        maskStyle,
        mask,
        maskClassName: 'mask-class-name',
      });
    },
    [Modal],
  );

  const openMask = React.useCallback(() => {
    openModal();
  }, [openModal]);

  const openNoMask = React.useCallback(() => {
    openModal(false);
  }, [openModal]);

  return (
    <div>
      <Button onClick={openMask}> Customized mask style </Button>
      <Button onClick={openNoMask}>No mask</Button>
    </div>
  );
};

ReactDOM.render(
  <ModalProvider>
    <App />
  </ModalProvider>,
  document.getElementById('container'),
);

