import React, { useState, useMemo } from 'react';
import ReactDOM from 'react-dom';
import {
  DataSet,
  Form,
  Switch,
  Button,
  TextField,
  NumberField,
} from 'choerodon-ui/pro';

const App = () => {
  const [autoCreate, setAutoCreate] = useState(false);
  const dataSet = useMemo(() => {
    return new DataSet({
      autoQuery: false,
      autoCreate,
      fields: [
        { name: 'name', type: 'string', label: '姓名' },
        { name: 'age', type: 'number', label: '年龄', defaultValue: 18 },
      ],
    });
  }, [autoCreate]);

  return (
    <div>
      autoCreate: <Switch checked={autoCreate} onChange={setAutoCreate} />
      <Form dataSet={dataSet} useColon>
        <TextField name="name" />
        <NumberField name="age" />
        <Button
          onClick={() => {
            dataSet.reset();
          }}
        >
          reset
        </Button>
        <Button
          onClick={() => {
            console.log('dataSet.current:', dataSet.current);
          }}
        >
          dataSet.current
        </Button>
      </Form>
    </div>
  );
};

ReactDOM.render(<App />, document.getElementById('container'));

