import React from 'react';
import ReactDOM from 'react-dom';
import {
  DataSet,
  Table,
  Modal,
  ModalProvider,
  injectModal,
  Button,
} from 'choerodon-ui/pro';

const { Column } = Table;

@injectModal
class App extends React.Component {
  friendsDs = new DataSet({
    queryUrl: '/dataset/user/queries',
    cascadeParams(parent) {
      console.log('cascadeParams', parent.toData());
      // 级联查询参数 (record, primaryKey) => object
      return {
        __parent: parent.toData(),
      };
    },
    fields: [
      { name: 'name', type: 'string', label: '姓名', required: true },
      { name: 'age', type: 'number', label: '年龄', step: 1 },
      {
        name: 'sex',
        type: 'string',
        label: '性别',
        lookupCode: 'HR.EMPLOYEE_GENDER',
        required: true,
      },
    ],
    events: {
      query: ({ params, data }) =>
        console.log('friend query parameter', params, data),
    },
  });

  enemyFriendsDs = new DataSet({
    selection: 'single',
    fields: [
      { name: 'name', type: 'string', label: '姓名', required: true },
      { name: 'age', type: 'number', label: '年龄', step: 1 },
      {
        name: 'sex',
        type: 'string',
        label: '性别',
        lookupCode: 'HR.EMPLOYEE_GENDER',
        required: true,
      },
      { name: 'code', type: 'object', label: '代码描述', lovCode: 'LOV_CODE' },
      { name: 'code_code', type: 'string', bind: 'code.code' },
      { name: 'code_description', type: 'string', bind: 'code.description' },
    ],
  });

  enemyDs = new DataSet({
    primaryKey: 'userid',
    autoQuery: false,
    autoCreate: true,
    fields: [
      { name: 'name', type: 'intl', label: '姓名', required: true },
      { name: 'age', type: 'number', label: '年龄', step: 1 },
      {
        name: 'sex',
        type: 'string',
        label: '性别',
        lookupCode: 'HR.EMPLOYEE_GENDER',
        required: true,
      },
    ],
    children: {
      friends: this.enemyFriendsDs,
    },
    events: {
      indexChange: ({ record }) =>
        record &&
        console.log('enemyRecord cascadeParent', record.cascadeParent),
    },
  });

  userDs = new DataSet({
    primaryKey: 'userid',
    transport: {
      read({ params: { page, pagesize } }) {
        return {
          url: `/dataset/user/page/${pagesize}/${page}`,
        };
      },
    },
    autoQuery: true,
    pageSize: 5,
    fields: [
      {
        name: 'userid',
        type: 'string',
        label: '编号',
        required: true,
      },
      {
        name: 'name',
        type: 'intl',
        label: '姓名',
      },
      {
        name: 'age',
        type: 'number',
        label: '年龄',
        max: 100,
        step: 1,
      },
      {
        name: 'sex',
        type: 'string',
        label: '性别',
        lookupCode: 'HR.EMPLOYEE_GENDER',
      },
      { name: 'enable', type: 'boolean', label: '是否开启' },
      { name: 'account', type: 'object', ignore: 'always' },
      {
        name: 'code',
        type: 'object',
        label: '代码描述',
        lovCode: 'LOV_CODE',
        transformRequest: (value) => value && value.code,
      },
      {
        name: 'code2',
        type: 'object',
        label: '代码描述2',
        lovCode: 'LOV_CODE',
        ignore: 'always',
        bind: 'code.lv',
      },
      {
        name: 'code2_description',
        type: 'string',
        label: '代码描述22',
        bind: 'code2.description',
      },
      {
        name: 'code_code',
        type: 'string',
        label: '代码',
        bind: 'code.code',
      },
      {
        name: 'code_description',
        type: 'string',
        label: '代码描述',
        bind: 'code.description',
      },
    ],
    events: {
      submit: ({ data }) => console.log('submit data', data),
      load: ({ dataSet }) => console.log('header ds', dataSet.slice()),
    },
    children: {
      friends: this.friendsDs,
      'other.enemy': this.enemyDs,
    },
  });

  openModal = (record) => {
    const { Modal: modal } = this.props;
    let isCancel = false;
    modal.open({
      drawer: true,
      closeOnLocationChange: false,
      width: 600,
      children: (
        <Table dataSet={this.friendsDs} rowHeight={40}>
          <Column name="name" />
          <Column name="age" editor sortable />
          <Column name="sex" editor width={150} />
        </Table>
      ),
      onOk: async () => (await Modal.confirm('ok?')) === 'ok',
      onCancel: () => (isCancel = true),
      afterClose: () => record && isCancel && this.userDs.remove(record),
    });
  };

  createUser = () => {
    this.openModal(this.userDs.create({}, 0));
    this.friendsDs.create({});
  };

  editUser = () => {
    this.openModal();
  };

  createButton = (
    <Button icon="playlist_add" onClick={this.createUser} key="add">
      新增
    </Button>
  );

  toData = () => {
    console.log('toData', this.userDs.toData());
  };

  toJSONData = () => {
    console.log('toJSONData', this.userDs.toJSONData());
  };

  toDataButton = (
    <Button onClick={this.toData} key="toData">
      toData
    </Button>
  );

  toJSONDataButton = (
    <Button onClick={this.toJSONData} key="toJSONData">
      toJSONData
    </Button>
  );

  renderEdit = () => {
    return (
      <Button
        funcType="flat"
        icon="mode_edit"
        onClick={this.editUser}
        size="small"
      />
    );
  };

  render() {
    const buttons = [
      this.createButton,
      'save',
      'delete',
      'query',
      this.toDataButton,
      this.toJSONDataButton,
    ];
    return [
      <Table
        key="user"
        buttons={buttons}
        dataSet={this.userDs}
        header="User"
        rowHeight="auto"
        columnDraggable
        rowNumber={({ text }) => `#${text}`}
      >
        <Column name="userid" editor />
        <Column name="age" editor width={150} />
        <Column name="enable" editor width={100} />
        <Column name="name" editor width={150} />
        <Column name="code" editor width={150} />
        <Column name="code2" editor width={150} />
        <Column name="code_code" editor width={170} />
        <Column name="code_description" editor width={150} />
        <Column
          header="编辑Friends"
          align="center"
          renderer={this.renderEdit}
          lock="right"
        />
      </Table>,
      <Table
        key="cascade1"
        header="Cascade Level 1 (enemy)"
        buttons={['add', 'delete']}
        dataSet={this.enemyDs}
        pagination={{ position: 'both' }}
      >
        <Column name="name" editor sortable />
        <Column name="age" editor sortable />
        <Column name="sex" editor width={150} />
      </Table>,
      <Table
        key="cascade2"
        header="Cascade Level 2 (enemyFriend)"
        buttons={['add', 'delete']}
        dataSet={this.enemyFriendsDs}
        pagination={{ position: 'top' }}
      >
        <Column name="name" />
        <Column name="age" editor sortable />
        <Column name="code" editor width={150} />
        <Column name="sex" editor width={150} />
      </Table>,
    ];
  }
}

ReactDOM.render(
  <ModalProvider>
    <App />
  </ModalProvider>,
  document.getElementById('container'),
);

