import React, { useState, useMemo } from 'react';
import ReactDOM from 'react-dom';
import { DataSet, Table, Form, SelectBox, Button } from 'choerodon-ui/pro';

const { Option } = SelectBox;

const TableDemo = (props) => {
  const { selection, selectionMode, alwaysShowRowBox } = props;
  const userDs = useMemo(() => {
    return new DataSet({
      primaryKey: 'userid',
      name: 'user',
      selection,
      autoQuery: true,
      pageSize: 5,
      fields: [
        {
          name: 'userid',
          type: 'string',
          label: '编号',
          required: true,
        },
        {
          name: 'name',
          type: 'intl',
          label: '姓名',
        },
        {
          name: 'age',
          type: 'number',
          label: '年龄',
          max: 100,
          step: 1,
        },
        {
          name: 'sex',
          type: 'string',
          label: '性别',
          lookupCode: 'HR.EMPLOYEE_GENDER',
          required: true,
        },
        { name: 'enable', type: 'boolean', label: '是否开启' },
      ],
    });
  }, [selection, selectionMode, alwaysShowRowBox]);

  const columns = useMemo(() => {
    return [
      { name: 'userid' },
      { name: 'name', editor: true },
      { name: 'age', editor: true },
      { name: 'sex', editor: true },
      { name: 'enable', editor: true },
    ];
  }, []);

  const showDataSetSelect = useMemo(() => {
    return (
      <Button
        key="showSelected"
        onClick={() => console.log('DataSet selected:', userDs.selected)}
      >
        打印选中Records
      </Button>
    );
  }, [userDs]);

  const buttons = useMemo(() => [showDataSetSelect], [showDataSetSelect]);

  return (
    <Table
      key="basic"
      rowNumber={({ text }) => `#${text}`}
      dataSet={userDs}
      columns={columns}
      buttons={buttons}
      selectionMode={selectionMode}
      alwaysShowRowBox={alwaysShowRowBox}
    />
  );
};

const App = () => {
  const [selection, setSelection] = useState('multiple');
  const [selectionMode, setSelectionMode] = useState('rowbox');
  const [alwaysShowRowBox, setAlwaysShowRowBox] = useState(false);

  return (
    <div>
      <Form key="form1" labelWidth="auto" useColon>
        <SelectBox
          key="selection"
          label="DataSet selection"
          value={selection}
          onChange={setSelection}
        >
          <Option value="multiple">multiple</Option>
          <Option value="single">single</Option>
          <Option value={false}>false</Option>
        </SelectBox>
        <SelectBox
          key="selectionMode"
          label="Table selectionMode"
          value={selectionMode}
          onChange={setSelectionMode}
        >
          <Option value="rowbox">rowbox</Option>
          <Option value="treebox">treebox</Option>
          <Option value="click">click</Option>
          <Option value="dblclick">dblclick</Option>
          <Option value="mousedown">mousedown</Option>
          <Option value="none">none</Option>
        </SelectBox>
        <SelectBox
          key="alwaysShowRowBox"
          label="DataSet alwaysShowRowBox"
          value={alwaysShowRowBox}
          onChange={setAlwaysShowRowBox}
        >
          <Option value>true</Option>
          <Option value={false}>false</Option>
        </SelectBox>
      </Form>
      <TableDemo
        selection={selection}
        selectionMode={selectionMode}
        alwaysShowRowBox={alwaysShowRowBox}
      />
    </div>
  );
};

ReactDOM.render(<App />, document.getElementById('container'));

