import React, { useMemo, useCallback } from 'react';
import ReactDOM from 'react-dom';
import { DataSet, Button, Table } from 'choerodon-ui/pro';

const App = () => {
  const ds = useMemo(() => {
    return new DataSet({
      autoQuery: false,
      pageSize: 10,
      validationRules: [
        { name: 'minLength', value: 3, message: '记录太短啦', disabled: false },
        {
          name: 'maxLength',
          value: 6,
          message: '记录太长啦',
          disabled: ({ dataSet }) => false,
        },
      ],
      fields: [
        { name: 'name', type: 'string', label: '姓名' },
        { name: 'age', type: 'number', label: '年龄' },
      ],
    });
  }, []);

  const handleValidateClick = useCallback(() => {
    ds.validate();
  }, [ds]);

  const validate = <Button onClick={handleValidateClick}>validate</Button>;

  return (
    <Table
      dataSet={ds}
      buttons={['add', 'delete', validate]}
      columns={[{ name: 'name' }, { name: 'age' }]}
    />
  );
};

ReactDOM.render(<App />, document.getElementById('container'));

