import React from 'react';
import ReactDOM from 'react-dom';
import { DataSet, Lov, Form, Switch, Button, Select } from 'choerodon-ui/pro';

const App = () => {
  const [paging, setPaging] = React.useState(true);

  const [allowQuery, setAllowQuery] = React.useState(true);

  // 下拉数据源
  const optionsDS = new DataSet({
    fields: [
      { name: 'meaing', type: 'string' },
      { name: 'value', type: 'string' },
    ],
    data: [
      { meaning: '男', value: 'M' },
      { meaning: '女', value: 'F' },
    ],
  });

  const ds = React.useMemo(() => {
    return new DataSet({
      autoCreate: true,
      fields: [
        { name: 'simpleSelect', options: optionsDS, label: '性别' },
        {
          name: 'code',
          textField: 'code',
          type: 'object',
          lovCode: 'LOV_CODE',
          lovPara: { code: '111' },
          required: true,
          optionsProps: (dsProps) => {
            console.log('dsProps:', dsProps);
            return {
              ...dsProps,
              paging,
              events: {
                query() {
                  console.log('do query ...');
                  return allowQuery;
                },
              },
            };
          },
        },
      ],
    });
  }, [paging, allowQuery]);

  return (
    <Form dataSet={ds}>
      <Switch checked={paging} onChange={setPaging} label="允许分页" />
      <Switch checked={allowQuery} onChange={setAllowQuery} label="允许查询" />
      <Select name="simpleSelect" />
      <Lov label="TEST_LOV" name="code" noCache />
      <div>
        <Button
          onClick={() => {
            console.log(
              '值集数据源:',
              ds.getField('code').getOptions(ds.current),
            );
          }}
        >
          获取值集数据源
        </Button>
      </div>
    </Form>
  );
};

ReactDOM.render(<App />, document.getElementById('container'));

