import React from 'react';
import ReactDOM from 'react-dom';
import {
  DataSet,
  Form,
  NumberField,
  Currency,
  TextField,
  CodeArea,
} from 'choerodon-ui/pro';
import { observer } from 'mobx-react';

const App = observer(() => {
  const [precision, setPrecision] = React.useState();
  const [minimumFractionDigits, setMinimumFractionDigits] = React.useState();
  const [maximumFractionDigits, setMaximumFractionDigits] = React.useState();
  const [step, setStep] = React.useState();
  const [currency, setCurrency] = React.useState('CNY');
  const getOptions = React.useCallback(() => {
    const options = {};
    if (minimumFractionDigits) {
      options.minimumFractionDigits = minimumFractionDigits;
    }
    if (maximumFractionDigits) {
      options.maximumFractionDigits = maximumFractionDigits;
    }
    return { options };
  }, [minimumFractionDigits, maximumFractionDigits]);
  const ds = React.useMemo(
    () =>
      new DataSet({
        autoCreate: true,
        fields: [
          {
            name: 'numberField',
            type: 'number',
            label: '数值',
            defaultValue: 3.1415926,
            precision,
            step,
            formatterOptions: getOptions(),
          },
          {
            name: 'currency',
            type: 'number',
            label: '金额',
            defaultValue: 3.1415926,
            precision,
            step,
            currency,
            formatterOptions: getOptions(),
          },
        ],
      }),
    [precision, getOptions, step, currency],
  );

  return (
    <>
      <Form header="示例" dataSet={ds} columns={2}>
        <NumberField name="numberField" />
        <Currency name="currency" />
      </Form>
      <Form header="属性设置" columns={2}>
        <NumberField
          label="prcision"
          value={precision}
          onChange={setPrecision}
        />
        <NumberField label="step" value={step} onChange={setStep} />
        <NumberField
          label="minimumFractionDigits"
          value={minimumFractionDigits}
          onChange={setMinimumFractionDigits}
        />
        <NumberField
          label="maximumFractionDigits"
          value={maximumFractionDigits}
          onChange={setMaximumFractionDigits}
        />
        <TextField label="currency" value={currency} onChange={setCurrency} />
      </Form>
      <Form header="实际值">
        <CodeArea value={JSON.stringify(ds.toData())} />
      </Form>
    </>
  );
});

ReactDOM.render(<App />, document.getElementById('container'));

