import React from 'react';
import ReactDOM from 'react-dom';
import {
  DataSet,
  Table,
  Form,
  Switch,
  SelectBox,
  NumberField,
  TextField,
  Button,
  message,
} from 'choerodon-ui/pro';

const App = () => {
  const [dynamic, setDynamic] = React.useState(false);
  const [disabled, setDisabled] = React.useState(true);
  const [disabledName, setDisabledName] = React.useState('Arthur');
  const [recordIdx, setRecordIdx] = React.useState(0);
  const [fieldName, setFieldName] = React.useState('');
  const [newValue, setNewValue] = React.useState('');
  const recordProps = React.useMemo(() => {
    if (dynamic) {
      return {
        dynamicProps: {
          disabled: (record) => record.get('name') === disabledName,
        },
      };
    }
    return { disabled };
  }, [dynamic, disabled, disabledName]);
  const ds = React.useMemo(
    () =>
      new DataSet({
        record: recordProps,
        fields: [
          { name: 'name', type: 'string', label: '姓名' },
          { name: 'age', type: 'number', label: '年龄' },
        ],
        data: [
          { name: 'Tommy', age: 44 },
          { name: 'Arthur', age: 45 },
        ],
      }),
    [recordProps],
  );

  const columns = React.useMemo(
    () => [
      { name: 'name', editor: true },
      { name: 'age', editor: true },
    ],
    [],
  );

  const handleSetRecord = React.useCallback(() => {
    const flag =
      recordIdx < ds.length - 1 &&
      fieldName.length > 0 &&
      newValue.length > 0 &&
      !!ds.get(recordIdx) &&
      !!ds.get(recordIdx).get(fieldName);
    if (flag) {
      ds.get(recordIdx).set(fieldName, newValue);
      message.success('修改成功');
    } else {
      message.error('输入信息有误!');
    }
  }, [recordIdx, fieldName, newValue, ds]);

  return (
    <>
      <Form>
        <Switch label="dynamic" checked={dynamic} onChange={setDynamic} />
        <SelectBox
          label="disabledName"
          value={disabledName}
          onChange={setDisabledName}
          disabled={!dynamic}
        >
          <SelectBox.Option value="Arthur">Arthur</SelectBox.Option>
          <SelectBox.Option value="Tommy">Tommy</SelectBox.Option>
        </SelectBox>
        <Switch
          label="disabled"
          checked={disabled}
          onChange={setDisabled}
          disabled={dynamic}
        />
      </Form>
      ds.length
      <Table dataSet={ds} columns={columns} />
      <Form columns={3} header="调用ds.set修改表格数据">
        <NumberField
          required
          help="index < ds.length"
          value={recordIdx}
          onChange={setRecordIdx}
          label="记录索引"
        />
        <TextField
          required
          help="name / age"
          value={fieldName}
          onChange={setFieldName}
          label="字段"
        />
        <TextField
          required
          help="anything"
          value={newValue}
          onChange={setNewValue}
          label="新值"
        />
        <div>
          <Button onClick={handleSetRecord}>
            ds.get(记录索引).set(字段, 新值)
          </Button>
          <Button onClick={() => ds.reset()}>重置</Button>
        </div>
      </Form>
    </>
  );
};

ReactDOM.render(<App />, document.getElementById('container'));

