examples.source:
import React from 'react';
import ReactDOM from 'react-dom';
import { DataSet, Select, Button, Row, Col } from 'choerodon-ui/pro';

function handleDataSetChange({ record, name, value, oldValue }) {
  console.log(
    '[dataset newValue]',
    value,
    '[oldValue]',
    oldValue,
    `[record.get('${name}')]`,
    record.get(name),
  );
}

function handleOption({ record }) {
  return {
    disabled: record.index === 0,
  };
}

class App extends React.Component {
  flag = false;

  ds = new DataSet({
    autoCreate: true,
    fields: [
      { name: 'sex', type: 'string', lookupCode: 'HR.EMPLOYEE_GENDER' },
      {
        name: 'sex2',
        type: 'string',
        dynamicProps: {
          lookupAxiosConfig: ({ record }) => ({
            url: record.get('sex') ? '/common/code/HR.EMPLOYEE_GENDER/' : null,
            transformResponse(data) {
              console.log('transformResponse', data);
              return data;
            },
          }),
        },
      },
      {
        name: 'lov2',
        type: 'string',
        lookupCode: 'SHI',
        defaultValue: ['QP', 'XH', 'HD'],
        multiple: true,
      },
    ],
    events: {
      update: handleDataSetChange,
    },
  });

  changeLookupCode = () => {
    this.flag = !this.flag;
    this.ds
      .getField('sex')
      .set('lookupCode', this.flag ? 'SYS.USER_STATUS' : 'HR.EMPLOYEE_GENDER');
  };

  render() {
    return (
      <>
        <Row gutter={10}>
          <Col span={6}>
            <Select
              dataSet={this.ds}
              name="sex"
              placeholder="请选择"
              onOption={handleOption}
            />
          </Col>
          <Col span={6}>
            <Button onClick={this.changeLookupCode}>修改lookupCode</Button>
          </Col>
          <Col span={12}>
            <Select dataSet={this.ds} name="sex2" placeholder="请选择" />
          </Col>
        </Row>
        <Row style={{ marginTop: 10 }}>
          <Col span={12}>
            <Select
              dataSet={this.ds}
              name="lov2"
              placeholder="请选择"
              maxTagCount={2}
              maxTagTextLength={3}
              maxTagPlaceholder={(restValues) => `+${restValues.length}...`}
            />
          </Col>
        </Row>
      </>
    );
  }
}

ReactDOM.render(<App />, document.getElementById('container'));

