import React from 'react';
import ReactDOM from 'react-dom';
import { DataSet, Select, Row, Col } from 'choerodon-ui/pro';

const { Option } = Select;

const data = [
  {
    'first-name': 'zhangsan',
  },
];

class App extends React.Component {
  ds = new DataSet({
    data,
    fields: [{ name: 'first-name', readOnly: true }],
  });

  render() {
    return (
      <Row gutter={10}>
        <Col span={12}>
          <Select
            name="last-name"
            placeholder="请选择"
