examples.source:
import React from 'react';
import ReactDOM from 'react-dom';
import { Button } from 'choerodon-ui';

ReactDOM.render(
  <div>
    <Button style={{ marginRight: 10 }} funcType="raised">
      Rasied
    </Button>
    <Button style={{ marginRight: 10 }} funcType="flat">
      Flat
    </Button>
    <Button
      style={{ marginRight: 10 }}
      shape="circle"
      funcType="flat"
      icon="search"
    />
  </div>,
  document.getElementById('container'),
);

