examples.source:
import React from 'react';
import ReactDOM from 'react-dom';
import { Button, Radio, Icon } from 'choerodon-ui';

class ButtonSize extends React.Component {
  state = {
    size: 'large',
  };

  handleSizeChange = (e) => {
    this.setState({ size: e.target.value });
  };

  render() {
    const { size } = this.state;
    return (
      <div>
        <Radio.Group value={size} onChange={this.handleSizeChange}>
          <Radio.Button value="large">Large</Radio.Button>
          <Radio.Button value="default">Default</Radio.Button>
          <Radio.Button value="small">Small</Radio.Button>
        </Radio.Group>
        <br />
        <br />
        <Button style={{ marginRight: 10 }} type="primary" size={size}>
          默认
        </Button>
        <Button style={{ marginRight: 10 }} size={size}>
          Normal
        </Button>
        <Button style={{ marginRight: 10 }} type="dashed" size={size}>
          Dashed
        </Button>
        <Button style={{ marginRight: 10 }} type="danger" size={size}>
          Danger
        </Button>
        <br />
        <Button
          style={{ marginRight: 10 }}
          type="primary"
          shape="circle"
          icon="cloud_queue"
          size={size}
        />
        <Button
          style={{ marginRight: 10 }}
          type="primary"
          icon="cloud_queue"
          size={size}
        >
          Download
        </Button>
        <br />
        <Button.Group size={size}>
          <Button style={{ marginRight: 10 }} type="primary">
            <Icon type="keyboard_arrow_left" />
            Backward
          </Button>
          <Button style={{ marginRight: 10 }} type="primary">
            Forward
            <Icon type="keyboard_arrow_right" />
          </Button>
        </Button.Group>
      </div>
    );
  }
}

ReactDOM.render(<ButtonSize />, document.getElementById('container'));

