examples.source:
import React from 'react';
import ReactDOM from 'react-dom';
import { DataSet, Button } from 'choerodon-ui/pro';
import { runInAction } from 'mobx';

class App extends React.Component {
  ds = new DataSet();

  state = {
    loading: true,
  };

  componentWillMount() {
    runInAction(() => {
      this.ds.status = 'submitting';
    });
  }

  handleClick = () => {
    this.setState({ loading: !this.state.loading });
    return new Promise((resolve) => setTimeout(resolve, 1000));
  };

  render() {
    return (
      <div>
        <Button icon="save" loading={this.state.loading}>
          保存
        </Button>
        <Button funcType="flat" color="primary" icon="save" loading>
          保存
        </Button>
        <Button icon="save" loading />
        <Button funcType="flat" icon="save" color="primary" loading />
        <Button dataSet={this.ds} type="submit">
          数据源状态
        </Button>
        <Button onClick={this.handleClick}>wait promise</Button>
      </div>
    );
  }
}

ReactDOM.render(<App />, document.getElementById('container'));

