examples.source:
import React from 'react';
import ReactDOM from 'react-dom';
import { Button } from 'choerodon-ui';

class App extends React.Component {
  state = {
    loading: false,
    iconLoading: false,
  };

  enterLoading = () => {
    this.setState({ loading: true });
  };

  enterIconLoading = () => {
    this.setState({ iconLoading: true });
  };

  render() {
    return (
      <span>
        <Button style={{ marginRight: 10 }} type="primary" loading>
          Loading
        </Button>
        <Button
          style={{ marginRight: 10 }}
          type="primary"
          funcType="raised"
          loading
        >
          Loading
        </Button>
        <Button
          style={{ marginRight: 10 }}
          type="primary"
          loading={this.state.loading}
          onClick={this.enterLoading}
        >
          Click me!
        </Button>
        <Button
          style={{ marginRight: 10 }}
          type="primary"
          icon="poweroff"
          loading={this.state.iconLoading}
          onClick={this.enterIconLoading}
        >
          Click me!
        </Button>
        <Button style={{ marginRight: 10 }} shape="circle" loading />
        <Button
          style={{ marginRight: 10 }}
          type="primary"
          shape="circle"
          loading
        />
      </span>
    );
  }
}

ReactDOM.render(<App />, document.getElementById('container'));

