examples.source:
import React from 'react';
import ReactDOM from 'react-dom';
import { Tree } from 'choerodon-ui';

function dig(path = '0', level = 3) { 
  const list = [];
  for (let i = 0; i < 10; i += 1) {
    const key = `${path}-${i}`;
    const treeNode = {
      title: key,
      key,
    };

    if (level > 0) {
      treeNode.children = dig(key, level - 1);
    }

    list.push(treeNode);
  }
  return list;
