examples.source:
import React from 'react';
import ReactDOM from 'react-dom';
import { Button, Tooltip } from 'choerodon-ui/pro';

const App = () => {
  const [flag, setFlag] = React.useState(true);
  const handleClick = React.useCallback(() => setFlag(!flag), [flag]);
  const overflowStyle = React.useMemo(
    () => ({
      maxWidth: 100,
      overflow: 'hidden',
      textOverflow: 'ellipsis',
      whiteSpace: 'nowrap',
    }),
    [],
  );
  return (
    <>
      <Tooltip title="外部包裹tooltip" placement="bottom">
        <Button tooltip="always" disabled>
          始终显示气泡的按钮
        </Button>
      </Tooltip>
      <Button
        tooltip="overflow"
        style={flag ? overflowStyle : undefined}
        onClick={handleClick}
      >
        内容超长时显示气泡&lt;点击改变样式&gt;
      </Button>
      <Button
        href="https://choerodon.io"
        target="_blank"
        tooltip="always"
        disabled
      >
        跳转按钮
      </Button>
    </>
  );
};

ReactDOM.render(<App />, document.getElementById('container'));

