import React, { useMemo, useCallback } from 'react';
import ReactDOM from 'react-dom';
import { DataSet, Table, Button } from 'choerodon-ui/pro';

const App = () => {
  const ds = useMemo(() => {
    return new DataSet({
      autoQuery: false,
      fields: [
        { name: 'name', type: 'string', label: '姓名' },
        { name: 'age', type: 'number', label: '年龄' },
      ],
      data: [
        { name: 'tom', age: 20 },
        { name: 'jerry', age: 23 },
      ],
    });
  }, []);

  const handleDataSetGetField = useCallback(() => {
    const field = ds.getField('name');
    // 给所有记录的 name 字段的 disabled 属性设置为 true
    field.set('disabled', true);
  }, [ds]);

  const handleRecordGetField = useCallback(() => {
    if (ds.records.length) {
      const recordField = ds.records[0].getField('age');
      // 给当前记录的 age 字段的 disabled 属性设置为 true
      recordField.set('disabled', true);
    }
  }, [ds]);

  const columns = useMemo(() => {
    return [
      { name: 'name', editor: true },
      { name: 'age', editor: true },
    ];
  }, []);

  return (
    <div>
      <Button onClick={handleDataSetGetField}>DataSet getField</Button>
      <Button onClick={handleRecordGetField}>Record getField</Button>
      <Table dataSet={ds} columns={columns} />
    </div>
  );
};

ReactDOM.render(<App />, document.getElementById('container'));

