import React, { useMemo, useState } from 'react';
import ReactDOM from 'react-dom';
import {
  DataSet,
  Form,
  TextField,
  NumberField,
  Button,
  CodeArea,
  Switch,
} from 'choerodon-ui/pro';

const App = () => {
  const [result, setResult] = useState('');
  const [submitUrl, setSubmitUrl] = useState(
    'https://www.fastmock.site/mock/423302b318dd24f1712751d9bfc1cbbc/mock/datasetapi/dsmthods/submit',
  );
  const [hasSubmitUrl, setHasSubmitUrl] = useState(true);

  const ds = useMemo(() => {
    return new DataSet({
      autoCreate: true,
      submitUrl: hasSubmitUrl ? submitUrl : undefined,
      fields: [
        { name: 'name', type: 'string', label: '姓名', required: true },
        { name: 'age', type: 'number', label: '年龄' },
      ],
    });
  }, [hasSubmitUrl, submitUrl]);

  const handleSubmit = async (type) => {
    let res = '';
    try {
      if (type === 'force') {
        res = await ds.forceSubmit();
      } else {
        res = await ds.submit();
      }
    } catch (err) {
      res = err.toString();
    }
    if (typeof res === 'undefined') {
      setResult('undefined');
    } else {
      setResult(JSON.stringify(res));
    }
  };

  const resultHelp = useMemo(() => {
    if (result.includes('Error')) {
      return '捕获到错误，请检查';
    }
    if (result.includes('"success":true')) {
      return '提交成功';
    }
    switch (result) {
      case 'undefined':
        return '返回值为 undefined，可能为无数据提交或提交相关配置不全，如缺少 submitUrl';
      case 'false':
        return '返回值为 false，校验未通过';
      default:
        return '';
    }
  }, [result]);

  return (
    <>
      <Form dataSet={ds} columns={1}>
        <TextField
          label="submitUrl"
          value={submitUrl}
          onChange={setSubmitUrl}
        />
        <Switch
          label="传入submitUrl"
          checked={hasSubmitUrl}
          onChange={setHasSubmitUrl}
        />

        <TextField name="name" />
        <NumberField name="age" />
        <div>
          <Button onClick={() => handleSubmit('normal')}>Submit</Button>
          <Button onClick={() => handleSubmit('force')}>ForceSubmit</Button>
        </div>
        <CodeArea
          label="提交返回"
          value={result}
          help={resultHelp}
          showHelp="newLine"
        />
      </Form>
    </>
  );
};

ReactDOM.render(<App />, document.getElementById('container'));

