import React, { useMemo, useState, useCallback } from 'react';
import ReactDOM from 'react-dom';
import {
  DataSet,
  Table,
  SelectBox,
  Row,
  Col,
  Button,
  CodeArea,
} from 'choerodon-ui/pro';

const { Column } = Table;
const { Option } = SelectBox;

const App = () => {
  const [selection, setSelection] = useState('multiple');
  const [selectionMode, setSelectionMode] = useState('rowbox');
  const [selectedCount, setSelectedCount] = useState(0);
  const ds = useMemo(() => {
    return new DataSet({
      primaryKey: 'userid',
      name: 'user',
      autoQuery: true,
      pageSize: 5,
      fields: [
        {
          name: 'userid',
          type: 'string',
          label: '编号',
          required: true,
        },
        {
          name: 'name',
          type: 'intl',
          label: '姓名',
        },
        {
          name: 'age',
          type: 'number',
          label: '年龄',
          max: 100,
          step: 1,
        },
      ],
      selection: selection === 'false' ? false : selection,
    });
  }, [selection, selectionMode]);

  const handleSelectionChange = (value) => {
    setSelection(value);
    setSelectedCount(0);
  };

  const handleSelectionModeChange = (value) => {
    setSelectionMode(value);
    setSelectedCount(0);
  };

  const handleButtonClick = useCallback(() => {
    setSelectedCount(ds.selected.length);
  }, [ds]);

  return (
    <>
      <Row>
        <Col span={4}>
          <span>selection:</span>
        </Col>
        <Col span={20}>
          <SelectBox value={selection} onChange={handleSelectionChange}>
            <Option value="false">false</Option>
            <Option value="multiple">multiple</Option>
            <Option value="single">single</Option>
          </SelectBox>
        </Col>
      </Row>
      <Row>
        <Col span={4}>
          <span>selectionMode:</span>
        </Col>
        <Col span={20}>
          <SelectBox value={selectionMode} onChange={handleSelectionModeChange}>
            <Option value="rowbox">rowbox</Option>
            <Option value="treebox">treebox</Option>
            <Option value="click">click</Option>
            <Option value="dblclick">dblclick</Option>
            <Option value="mousedown">mousedown</Option>
            <Option value="none">none</Option>
          </SelectBox>
        </Col>
      </Row>
      <Table dataSet={ds} selectionMode={selectionMode}>
        <Column name="name" />
        <Column name="age" />
      </Table>
      <Button onClick={handleButtonClick}>查看选中条数</Button>
      <CodeArea value={selectedCount} />
    </>
  );
};

ReactDOM.render(<App />, document.getElementById('container'));

