import React from 'react';
import ReactDOM from 'react-dom';
import {
  DataSet,
  DatePicker,
  DateTimePicker,
  Button,
  Row,
  Col,
} from 'choerodon-ui/pro';

function handleDataSetChange({ value, oldValue }) {
  console.log('[range dataset newValue]', value, '[oldValue]', oldValue);
}

class App extends React.Component {
  ds = new DataSet({
    autoCreate: true,
    fields: [
      {
        name: 'date',
        type: 'date',
        range: ['start', 'end'],
        defaultValue: { start: '1984-11-22', end: new Date() },
        required: true,
      },
      {
        name: 'date2',
        type: 'date',
        range: true,
      },
      {
        name: 'dateRange',
        type: 'date',
        ignore: 'always',
        range: ['start', 'end'],
        label: 'dateRange',
      },
      {
        name: 'dateFrom',
        type: 'date',
        bind: 'dateRange.start',
      },
      {
        name: 'dateTo',
        type: 'date',
        bind: 'dateRange.end',
      },
    ],
    events: {
      update: handleDataSetChange,
    },
  });

  render() {
    return (
      <Row gutter={10}>
        <Col style={{ marginBottom: 10 }} span={24}>
          <DateTimePicker dataSet={this.ds} name="date" />
        </Col>
        <Col style={{ marginBottom: 10 }} span={24}>
          <DatePicker
            dataSet={this.ds}
            name="date2"
            placeholder={['Start Date', 'End Date']}
          />
        </Col>
        <Col style={{ marginBottom: 10 }} span={24}>
          <DatePicker
            dataSet={this.ds}
            name="dateRange"
            placeholder={['dateRange Start', 'dateRange End']}
          />
        </Col>
        <Col style={{ marginBottom: 10 }} span={24}>
          <Button
            onClick={() => console.log('toJSONData', this.ds.toJSONData())}
          >
            toJSONData()
          </Button>
        </Col>
      </Row>
    );
  }
}

ReactDOM.render(<App />, document.getElementById('container'));

