import React from 'react';
import ReactDOM from 'react-dom';
import { DataSet, Form, NumberField, Select, Button } from 'choerodon-ui/pro';

const { Option } = Select;

class App extends React.Component {
  ds = new DataSet({
    autoCreate: true,
    events: {
      update: ({ record, name, value }) => {
        console.log('update', record, name, value, record.get('language'));
      },
    },
    fields: [
      { name: 'score', type: 'number', label: '分数一' },
      {
        name: 'scoreAll',
        type: 'number',
        label: '汇总分',
        transformRequest: (value, record) => {
          const score1 = record.get('score');
          console.log('汇总分, score', score1, 'all', value + score1);
          if (score1) {
            return value + score1;
          }
          return value;
        },
      },
      {
        name: 'language',
        type: 'string',
        multiple: true,
        label: '语言',
        transformRequest: (v) => {
          console.log('语言', v);
          return v;
        },
      },
      {
        name: 'a',
        bind: 'language',
        transformRequest: (v) => {
          console.log('语言 + bind', v);
          return v;
        },
      },
    ],
  });

  render() {
    return (
      <Form dataSet={this.ds} style={{ width: '4rem' }}>
        <NumberField name="score" />
        <NumberField name="scoreAll" />
        <Select name="language">
          <Option value="zh-cn">简体中文</Option>
          <Option value="en-us">英语(美国)</Option>
          <Option value="ja-jp">日本語</Option>
        </Select>
        <div>
          <Button onClick={() => this.ds.submit()}>提交</Button>
          <Button type="reset" style={{ marginLeft: 8 }}>
            重置
          </Button>
        </div>
      </Form>
    );
  }
}

ReactDOM.render(<App />, document.getElementById('container'));

