import React, { useMemo } from 'react';
import ReactDOM from 'react-dom';
import {
  DataSet,
  Form,
  TextField,
  NumberField,
  Button,
} from 'choerodon-ui/pro';

const App = () => {
  const ds = useMemo(() => {
    return new DataSet({
      autoCreate: true,
      fields: [
        {
          name: 'string-validate',
          label: 'min-length=3/max-length=10',
          minLength: 3,
          maxLength: 10,
        },
        {
          name: 'number-validate',
          label: 'min=3/max=10',
          min: 3,
          max: 10,
        },
        {
          name: 'pattern',
          label: 'pattern=/[a-z]/',
          pattern: /[a-z]/,
        },
        {
          name: 'restrict',
          label: 'restrict=/[a-z]/(受控)',
        },
        {
          name: 'validator',
          label: 'validator=()=>false',
          validator: (value, name, record) => {
            console.log('value, name, record:', value, name, record);
            return '始终校验失败';
          },
        },
        {
          name: 'required',
          label: 'required',
          required: true,
        },
      ],
    });
  }, []);

  return (
    <>
      <Form header="校验相关" dataSet={ds} columns={2} labelLayout="vertical">
        <TextField name="string-validate" />
        <NumberField name="number-validate" />
        <TextField name="pattern" />
        <TextField name="restrict" restrict={/[a-z]/} />
        <NumberField name="validator" />
        <NumberField name="required" />
        <div>
          <Button onClick={() => ds.reset()}>重置</Button>
        </div>
      </Form>
    </>
  );
};

ReactDOM.render(<App />, document.getElementById('container'));

