                case 'create':
                  return {
                    // url: `create api`,
                    method: 'put',
                    data: data[0],
                  };
                case 'update':
                  return {
                    // url: `update api`,
                    method: 'put',
                    data: data[0],
                  };
                case 'destroy':
                  return {
                    // url: `destroy api`,
                    url:
                      'https://www.fastmock.site/mock/423302b318dd24f1712751d9bfc1cbbc/mock/listDel',
                    data: data[0],
                    method: 'delete',
                  };
              }
            },
          },
        }}
        border={false}
        columns={this.columns}
      />
    );
  }
}

ReactDOM.render(<App />, document.getElementById('container'));

