import React from 'react';
import ReactDOM from 'react-dom';
import {
  DataSet,
  Table,
  Form,
  TextField,
  NumberField,
  SelectBox,
  Modal,
  Button,
  Icon,
} from 'choerodon-ui/pro';

const { Column } = Table;

const EditButton = (props) => {
  const handleClick = (e) => {
    const { record, onClick } = props;
    onClick(record, e);
  };

  return (
    <Button
      funcType="flat"
      icon="mode_edit"
      onClick={handleClick}
      size="small"
    />
  );
};

class App extends React.Component {
  state = { customizedCode: 'customized', heightChangeable: true };

  style = { height: 'calc(100vh - 100px)' };

  userDs = new DataSet({
    primaryKey: 'userid',
    transport: {
      read({ params: { page, pagesize } }) {
        return {
          url: `/dataset/user/page/${pagesize}/${page}`,
        };
      },
    },
    autoQuery: true,
    pageSize: 5,
    fields: [
      {
        name: 'userid',
        type: 'string',
        label: '编号',
        required: true,
      },
      {
        name: 'name',
        type: 'intl',
        label: '姓名',
      },
      {
        name: 'age',
        type: 'number',
        label: '年龄',
        max: 100,
        step: 1,
      },
      {
        name: 'sex',
        type: 'string',
        label: '性别',
        lookupCode: 'HR.EMPLOYEE_GENDER',
        required: true,
      },
      { name: 'enable', type: 'boolean', label: '是否开启' },
    ],
    events: {
      submit: ({ data }) => console.log('submit data', data),
    },
  });

  columnsDragRender = { renderIcon: () => <Icon type="open_with" /> };

  editUser = (record) => {
    this.openModal(record);
  };

  openModal = (record, isNew) => {
    let isCancel = false;
    Modal.open({
      drawer: true,
      width: 600,
      children: (
        <Form record={record}>
          <TextField name="userid" />
          <TextField name="name" />
          <NumberField name="age" />
          <SelectBox name="sex" />
        </Form>
      ),
      onOk: () => this.userDs.submit(),
      onCancel: () => {
        isCancel = true;
      },
      afterClose: () => isCancel && isNew && this.userDs.remove(record),
    });
  };

  renderEdit = ({ record }) => {
    return <EditButton onClick={this.editUser} record={record} />;
  };

  renderName = ({ text }) => {
    return new Array(3).fill(text).join(' ');
  };

  handleChangeCustomized = () => {
    const { customizedCode } = this.state;
    this.setState({
      customizedCode: customizedCode === 'customized' ? 'other' : 'customized',
    });
  };
  handleChangeheightChangeable = () => {
    const { heightChangeable } = this.state;
    this.setState({
      heightChangeable: !heightChangeable,
    });
  };

  render() {
    const { customizedCode, heightChangeable } = this.state;
    return (
      <>
        <Button onClick={this.handleChangeCustomized}>
          当前customizedCode： {customizedCode}
        </Button>
        <Button onClick={this.handleChangeheightChangeable}>
          当前handleChangeheightChangeable状态:
          {heightChangeable ? 'true' : 'false'}
        </Button>
        <Table
          // 开启个性化
          customizable
          border={false}
          heightChangeable={heightChangeable}
          // 表格个性化编码，全局配置  customizedSave | customizedLoad 个性化处理钩子，结合后端 API 进行存储
          customizedCode={customizedCode}
          rowHeight={40}
          key="user"
          dataSet={this.userDs}
          rowDraggable
          // 表格个性化内列拖拽
          columnDraggable
          // 表格个性化内编辑列头
          columnTitleEditable
          dragColumnAlign="left"
          columnsDragRender={this.columnsDragRender}
          style={this.style}
        >
          <Column header="组合">
            <Column header="子组合">
              <Column
                name="userid"
                title="ID"
                header={(ds, name, title) => <i>{title}</i>}
                tooltip="always"
              />
              <Column
                name="name"
                tooltip="overflow"
                renderer={this.renderName}
              />
            </Column>
          </Column>
          <Column
            header="操作"
            align="center"
            renderer={this.renderEdit}
            footer="---"
          />
          <Column header="组合" titleEditable={false}>
            <Column name="age" help="help" sortable tooltip="always" />
            <Column name="enable" tooltip="overflow" hideable={false} />
          </Column>
        </Table>
      </>
    );
  }
}

ReactDOM.render(<App />, document.getElementById('container'));

