import React from 'react';
import ReactDOM from 'react-dom';
import { Cascader } from 'choerodon-ui/pro';

const options = [
  {
    value: 'zhejiang',
    meaning: 'Zhejiang',
    children: [
      {
        value: 'hangzhou',
        meaning: 'Hangzhou',
        children: [
          {
            value: 'xihu',
            meaning: 'West Lake',
          },
        ],
      },
    ],
  },
  {
    value: 'jiangsu',
    meaning: 'Jiangsu',
    children: [
      {
        value: 'nanjing',
        meaning: 'Nanjing',
        children: [
