import React from 'react';
import ReactDOM from 'react-dom';
import { configure } from 'choerodon-ui';
import { DataSet, Lov, message } from 'choerodon-ui/pro';

configure({
  lovQueryCachedSelected() {
    return Promise.resolve([{ code: 'c7n', description: 'Choerodon UI' }]);
  },
});

function handleDataSetChange({ record, name, value, oldValue }) {
  console.log(
    '[dataset multiple]',
    value,
    '[oldValue]',
    oldValue,
    `[record.get('${name}')]`,
    record.get(name),
  );
}

class App extends React.Component {
  ds = new DataSet({
    primaryKey: 'code',
    data: [
      {
        code_code: 'HR.EMPLOYEE_GENDER, HR.EMPLOYEE_STATUS, c7n',
        code_description: '性别,员工状态,Choerodon UI',
      },
    ],
    fields: [
      {
        name: 'code',
        type: 'object',
        lovCode: 'LOV_CODE',
        multiple: true,
        required: true,
      },
      {
        name: 'code_code',
        type: 'string',
        bind: 'code.code',
        multiple: ',',
      },
      {
        name: 'code_description',
        type: 'string',
        bind: 'code.description',
        multiple: ',',
      },
    ],
    cacheSelection: true,
    selection: 'multiple',
    events: {
      update: handleDataSetChange,
    },
  });

  handleBeforeSelect = (records) => {
    if (!records.length) {
      message.warning('请选择至少一条记录');
      return false;
    }
  };

  render() {
    const tableProps = {
      style: {
        maxHeight: 'calc(100vh - 400px)',
      },
    };
    return (
      <Lov
        dataSet={this.ds}
        searchAction="blur"
        name="code"
        placeholder="复选LOV"
        tableProps={tableProps}
        onBeforeSelect={this.handleBeforeSelect}
      />
    );
  }
}

ReactDOM.render(<App />, document.getElementById('container'));

