    events: {
      update: handleDataSetChange,
    },
  });

  render() {
    const { ds } = this;
    return (
      <>
        <Row style={{ marginBottom: 10 }} gutter={10}>
          <Col span={6}>
            <Lov
              dataSet={ds}
              name="code"
              mode="button"
              clearButton={false}
              icon="check"
            >
              请选择
            </Lov>
          </Col>
          <Col span={10}>
            <Output dataSet={ds} name="code_code" />
          </Col>
          <Col span={6}>
            <Output dataSet={ds} name="code_description" />
          </Col>
        </Row>
        <Row gutter={10}>
          <Col span={24}>
            <Lov
              dataSet={ds}
              name="code_string"
              mode="button"
              placeholder="请选择"
            />
          </Col>
        </Row>
      </>
    );
  }
}

ReactDOM.render(<App />, document.getElementById('container'));

