examples.source:
import React from 'react';
import ReactDOM from 'react-dom';
import { DataSet, Select, Row, Col, Button } from 'choerodon-ui/pro';

function handleDataSetChange({ record, name, value, oldValue }) {
  console.log(
    '[dataset newValue]',
    value,
    '[oldValue]',
    oldValue,
    `[record.get('${name}')]`,
    record.get(name),
  );
}

class App extends React.Component {
  optionDs = new DataSet({
    selection: 'single',
    transport: {
      read({ params: { page, pagesize } }) {
        return {
          url: `/dataset/user/page/${pagesize}/${page}`,
        };
      },
    },
    autoQuery: true,
  });

  ds = new DataSet({
    data: [{ userid: '15', name: '戴刚' }],
    fields: [
      {
        name: 'user',
        type: 'object',
        textField: 'name',
        valueField: 'userid',
        label: '用户',
        options: this.optionDs,
        ignore: 'always',
      },
      {
        name: 'account',
        multiple: true,
      },
      {
        name: 'name',
        bind: 'user.name',
      },
      {
        name: 'userid',
        bind: 'user.userid',
      },
    ],
    events: {
      update: handleDataSetChange,
    },
  });

  optionNoPageDs = new DataSet({
    selection: 'single',
    queryUrl: '/dataset/user/queries',
    autoQuery: true,
    paging: false,
  });

  dsOne = new DataSet({
    fields: [
      {
        name: 'user',
        type: 'string',
        textField: 'name',
        valueField: 'userid',
        label: '用户',
        options: this.optionNoPageDs,
      },
    ],
  });

  changeOptions = () => {
    this.ds.addField('account', {
      name: 'account',
      type: 'string',
      textField: 'name',
      valueField: 'userid',
      label: '账户',
      options: this.optionDs,
    });
  };

  render() {
    return (
      <>
        <Row span={8}>分页加载：</Row>
        <Row gutter={10}>
          <Col span={8}>
            <Select
              multiple
              optionsFilter={(record) => record.get('sex') === 'F'}
              dataSet={this.ds}
              name="user"
            />
          </Col>
          <Col span={8}>
            <Select multiple dataSet={this.ds} name="account" />
          </Col>
          <Col span={8}>
            <Button onClick={this.changeOptions}>切换选项</Button>
          </Col>
        </Row>
        <Row span={8}>不分页：</Row>
        <Row gutter={10}>
          <Col span={8}>
            <Select dataSet={this.dsOne} name="user" />
          </Col>
        </Row>
      </>
    );
  }
}

ReactDOM.render(<App />, document.getElementById('container'));

